import User from "./User"
import { useEffect, useRef } from "react"

const Users = (props) => {
let form = {
    input: useRef (null),
    btnCreate: useRef(null)
}

    let UsersCollection = props.state.map((user, index) => {
        return (
            <User key = {index} name = {user.name} />
        )
    })

    useEffect(() => {
        form.btnCreate.current.addEventListener("click", () => {
            let newUser = {
                id: props.state[props.state.lenth -1].id + 1,
                name: form.input.current.value
            }
        })
    })

    return (
        <div>
            <h1>Users</h1>
            {UsersCollection}
            <hr />
            <input type="text" placeholder="Your name" name="" id="" />
            <button ref={form.btnCreate}></button>
        </div>
    )
}

export default Users
