const ADD_USER = "ADD-USER"

export const addUserAC = (user) => {
    return {
        type: ADD_USER,
        user
    }
}

export const UsersReduser = (state, action) => {
    if (action.type === ADD_USER) {
        return ({
            ...state,
            usersPage: [
                ...state.usersPage,
                {...action.user}
            ]
        })
    } else {
        return state
    }
}
